import React, { useState } from 'react';
import { Button, Toast, ToastBody, ToastHeader, Row, Col } from 'reactstrap';

const ToastSkills = (props) => {
  const { buttonLabel } = props;
  const [show, setShow] = useState(false);

  const toggle = () => setShow(!show);

  return (
    <div className="toastbody">

      <Button color="secondary" onClick={toggle}> Analyst </Button>
      <p> </p>
      <Button color="primary" onClick={toggle}> Developer </Button>

      <br/>
      <br/>

      <Row className="portrait">
        <Toast isOpen={show} className="col-sm">
          <ToastHeader toggle={toggle}>Analyst</ToastHeader>
          <ToastBody>
            Lorem ipsum.
          </ToastBody>
        </Toast>
        <Toast isOpen={show} className="col-sm">
        </Toast>
        <Toast isOpen={show} className="col-sm">
          <ToastHeader toggle={toggle}>Developer</ToastHeader>
          <ToastBody>
            Lorem ipsum dolor.
          </ToastBody>
        </Toast>
      </Row>


    </div>
  );
}

export default ToastSkills;
