    return (
      <div OnClick={this.switchSide} className={this.state.side[0].gauche}>

      <Button color="primary" OnClick={this.switchSide} className={this.state.side[0].gauche} >
        This is a primary alert — check it out!
      </Button>
      <Alert color="secondary" className={this.state.side.droite}>
        This is a primary alert — check it out!
      </Alert>

      </div>
    )
  }
}


export default Test
