import React, { Component } from 'react';
import { Alert, Button, Row} from 'reactstrap';
var classNames = require('classnames');


class Test extends Component {


  state = {
      background: [' b rouge']
  };

  rightSide = () => {
    this.setState({
      background: ['vert']
    });
  };

  leftSide = () => {
    this.setState({
      background: ['rouge']
    });
};



  render() {

    document.addEventListener('DOMContentLoaded', function(){
      const block = document.querySelector(".block");


      block.addEventListener("click", () => {
        console.log('ok click');
      });

      block.addEventListener("mouseenter", () => {
        console.log('ok');
      });
    });


/*
    document.addEventListener('DOMContentLoaded', function(){
      const gauche = document.querySelector(".gauche");
      const droite = document.querySelector(".droite");

      gauche.addEventListener("mouseenter", () => {
        console.log('ok');
        className += ' a';
        color = 'secondary';
        console.log({className});
      });

      droite.addEventListener("mouseenter", () => {
        console.log('ok droite');
      });

    });
*/
  return (

      <Row className="block portrait">
            <div className={this.state.background} onMouseEnter={this.rightSide} onMouseLeave={this.leftSide}>
                  <h1>The Designer</h1>
                  <Button href="#" class="button">Read More</Button>
            </div>
            <div className="droite vert">
                  <h1>The Programmer</h1>
                  <Button href="#" className="button">Read More</Button>
            </div>
      </Row>

    )
  }
}

export default Test
