import React, { Component } from 'react';
import { Alert, Button, Row} from 'reactstrap';
var classNames = require('classnames');


class Test extends Component {


  state = {
      droite: ['droite rouge'],
      gauche: ['gauche vert']
  };

  rightSide = () => {
    this.setState({
      droite: ['vert']
    });
  };

  leftSide = () => {
    this.setState({
      gauche: ['rouge']
    });
};



  render() {

    document.addEventListener('DOMContentLoaded', function(){
      const block = document.querySelector(".block");
      const droite = document.querySelector(".droite");
      const gauche = document.querySelector(".gauche");


      block.addEventListener("click", () => {
        console.log('ok click');
      });

      block.addEventListener("mouseenter", () => {
        console.log('ok');
      });
    });

  return (

      <Row className="block">
            <div className={this.state.gauche} onMouseEnter={this.rightSide} onMouseLeave={this.state.gauche}>
                  <h1>The Designer</h1>
                  <Button href="#" class="button">Read More</Button>
            </div>
            <div className={this.state.droite} onMouseEnter={this.leftSide} onMouseLeave={this.state.droite}>
                  <h1>The Programmer</h1>
                  <Button href="#" className="button">Read More</Button>
            </div>
      </Row>

    )
  }
}

export default Test
