import React, { Component } from 'react';
import './SplitScreen.module.css';
import TBS from './Logo_tbs.png';
import Ecole from './logo_42.png'
import {Button} from 'reactstrap';
var classNames = require('classnames');



class SplitScreen2 extends Component {
  render() {

      document.addEventListener('DOMContentLoaded', function(){
        const left = document.querySelector(".left");
        const right = document.querySelector(".right");
        const container = document.querySelector(".container");

        var shift = classNames({
          Oleft: false,
          Oright: false,
        });

          left.addEventListener("mouseenter", () => {
            console.log('ok');
            var shift = classNames({
              Oleft: true,
              Oright: false,
            });
          }),

          left.addEventListener("mouseleave", () => {
            var shift = classNames({
              Oleft: false,
              Oright: false,
            });
          }),

          right.addEventListener("mouseenter", () => {
            var shift = classNames({
              Oleft: true,
              Oright: false,
            });
          }),

          right.addEventListener("mouseleave", () => {
            var shift = classNames({
              Oleft: false,
              Oright: false,
            });
          });
      });

    return (
      <div className="container bodys">

            <div className="split left">
            <div className={shift}>

              <h1 className="H1s" >The Designer</h1>
              <Button href="#" className="button">Read More</Button>
            </div>
            </div>

            <div className="split right">
            <div className={shift}>

              <h1>The Programmer</h1>
              <Button href="#" className="button">Read More</Button>
            </div>
            </div>

      </div>
    );
  }
}

export default SplitScreen2;
